import React, { useState } from "react";

import { Container, Paper, Typography, TextField } from "@material-ui/core";

type PropsType = {
  loginHandler: (a: string, b: string) => void;
  authLoading: boolean;
};

function Signin(props: PropsType) {
  const { loginHandler, authLoading } = props;

  const [state, setState] = useState({
    email: "",
    password: "",
  });

  const formSubmitHandler = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (state.email === "" || state.password === "") {
      return;
    }
    loginHandler(state.email, state.password);
  };

  return (
    <Container>
      <Paper
        variant="outlined"
        style={{ padding: "1.25rem 1.875rem", marginTop: "3rem" }}
      >
        <Typography variant="h5">Signin</Typography>

        <form onSubmit={formSubmitHandler}style={{marginTop: '2rem'}}>
          <TextField fullWidth label="E-mail" style={{marginBottom: "1rem"}} margin={20} variant="filled" />
          <TextField fullWidth label="Password" variant="filled" />
        </form>
      </Paper>
    </Container>
  );
}

export default Signin;
