import React from "react";

import {
  Paper,
  TextField,
  makeStyles,
  Button,
  Box,
  CircularProgress,
} from "@material-ui/core";
import { TodoType } from "../../types/types";

type PropTypes = {
  inputChangeHandler: (e: React.ChangeEvent<HTMLInputElement>) => void;
  addTodoHandler: (e: React.FormEvent<HTMLFormElement>) => void;
  error: { titleError: boolean; descError: boolean };
  isTodoAdding: boolean;
  todo: TodoType;
};

function TodoInput(props: PropTypes) {
  const classes = useStyles();

  const { inputChangeHandler, addTodoHandler, error, isTodoAdding } = props;

  return (
    <Paper variant="outlined" className={classes.paper}>
      <form noValidate onSubmit={addTodoHandler}>
        <Box width="100%" display="flex" alignItems="center">
          <Box width="70%">
            <TextField
              label="Todo title"
              name="title"
              variant="outlined"
              onChange={inputChangeHandler}
              error={error.titleError}
              helperText={
                error.titleError ? "Can not be left empty, min words 8" : ""
              }
            />
            <TextField
              style={{ width: "70%", marginLeft: "1rem" }}
              name="description"
              label="Description"
              variant="outlined"
              onChange={inputChangeHandler}
              error={error.descError}
              helperText={
                error.descError ? "Can not be left empty, min words 16" : ""
              }
            />
          </Box>
          <Box width="30%" display="flex" justifyContent="flex-end">
            <Button
              type="submit"
              variant="outlined"
              color="primary"
              style={{ width: "6.5625rem" }}
            >
              {isTodoAdding ? <CircularProgress size={20} /> : "Add Todo"}
            </Button>
          </Box>
        </Box>
      </form>
    </Paper>
  );
}

export default TodoInput;

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: "2rem 1rem",
    marginTop: "1rem",
  },
}));
