import React, { useState } from "react";
import { Route, Switch } from "react-router";
import Signin from "./components/auth/Signin";
import Signup from "./components/auth/Signup";

import Layout from "./components/Layout/Layout";
import Todo from "./components/Todo/Todo";
import { axios } from "../src/http/http";
import { useToasts } from "react-toast-notifications";

function App() {
  const { addToast } = useToasts();

  const [state, setState] = useState<{
    isAuth: boolean;
    token: null | string;
    userId: null | string;
    authLoading: boolean;
    error:
      | null
      | string
      | Array<{ value: string; msg: string; param: string; location: string }>;
  }>({
    isAuth: false,
    token: null,
    userId: null,
    authLoading: false,
    error: null,
  });

  // login handler
  const loginHandler = async (email: string, password: string) => {
    try {
      setState((prevState) => {
        return { ...prevState, authLoading: true, isAuth: true };
      });
      const { data } = await axios.post<{ token: string; userId: string }>(
        "auth/signin",
        { email, password }
      );

      setState({
        isAuth: true,
        token: data.token,
        userId: data.userId,
        authLoading: false,
        error: null,
      });
      console.log(data);
    } catch (error) {
      setState((prevState) => {
        return { ...prevState, isAuth: false };
      });
    } finally {
      setState((prevState) => {
        return { ...prevState, authLoading: false };
      });
    }
  };

  // signup handler
  const signupHandler = async (
    email: string,
    password: string,
    name: string
  ) => {
    try {
      setState((prevState) => {
        return { ...prevState, authLoading: true };
      });
      const { data } = await axios.post<{
        success: boolean;
        message: string;
        userId: string;
        data: Array<{
          value: string;
          msg: string;
          param: string;
          location: string;
        }>;
      }>("auth/signup", {
        email,
        password,
        name,
      });
      if (data.success) {
        addToast(data.message ? data.message : "User created Successfully", {
          appearance: "success",
          autoDismiss: true,
        });
      } else if (data.data && !data.success) {
        setState((prevState) => {
          return { ...prevState, error: data.data };
        });
        throw new Error();
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      console.log("error", error);

      if (state.error && typeof Array.isArray(state.error)) {
        //@ts-ignore
        state.error.map((item) =>
          addToast(item.msg ? item.msg : "Something went wrong", {
            appearance: "error",
            autoDismiss: true,
          })
        );
        return
      }

      // addToast(error.message ? error.message : "Something went wrong", {
      //   appearance: "error",
      //   autoDismiss: true,
      // });
    } finally {
      setState((prevState) => {
        return { ...prevState, authLoading: false };
      });
    }
  };

  return (
    <div>
      <Layout>
        <Switch>
          <Route
            exact
            path="/"
            render={(props) => <Todo {...props} isAuth={state.isAuth} />}
          />
          <Route
            exact
            path="/signin"
            render={(props) => (
              <Signin
                {...props}
                loginHandler={loginHandler}
                authLoading={state.authLoading}
              />
            )}
          />
          <Route
            path="/signup"
            render={(props) => (
              <Signup
                {...props}
                signupHandler={signupHandler}
                authLoading={state.authLoading}
              />
            )}
          />
        </Switch>
      </Layout>
    </div>
  );
}

export default App;
