import React from "react";

import {
  Paper,
  makeStyles,
  Typography,
  Box,
  Tooltip,
  CircularProgress,
} from "@material-ui/core";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import DoneIcon from "@material-ui/icons/Done";

type PropTypes = {
  isDeleting: boolean;
  isMarkingAsCompleted: boolean;
};

function TodoCard(props: PropTypes) {
  const { isDeleting, isMarkingAsCompleted } = props;
  const classes = useStyles();

  return (
    <Paper className={classes.paper}>
      <Box>
        <Typography variant="h5">Complete this app</Typography>
        <Typography variant="caption">
          This is a mern stack app with nodejs and reactjs
        </Typography>
      </Box>

      <Box>
        {isMarkingAsCompleted ? (
          <CircularProgress size={20} />
        ) : (
          <Tooltip title="Mark as completed">
            <IconButton aria-label="complete">
              <DoneIcon />
            </IconButton>
          </Tooltip>
        )}
        &nbsp;
        {isDeleting ? (
          <CircularProgress size={20} />
        ) : (
          <Tooltip title="Delete">
            <IconButton aria-label="delete">
              <DeleteIcon />
            </IconButton>
          </Tooltip>
        )}
      </Box>
    </Paper>
  );
}

export default TodoCard;

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: "1rem 1rem",
    marginBottom: "2remF",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",

    "& p": {
      marginTop: "10px",
    },
  },
}));
