import React, { useEffect, useState } from "react";

import { Route, Switch } from "react-router";
import { Container } from "@material-ui/core";

import AllTodos from "./AllTodos";
import CompletedTodos from "./CompletedTodos";
import TodoHeader from "./TodoHeader";
import TodoInput from "./TodoInput";
import { TodoType } from "../../types/types";
import { axios } from "../../http/http";

function Todo() {
  const [todo, setTodo] = useState<{
    title: string;
    description: string;
    isCompleted: boolean;
  }>({
    title: "",
    description: "",
    isCompleted: false,
  });

  const [allTodos, setAllTodos] = useState<TodoType[]>([]);

  const [error, setError] = useState({
    titleError: false,
    descError: false,
  });

  const inputChangeHandler = (event: React.ChangeEvent<HTMLInputElement>) => {
    setError({ titleError: false, descError: false });
    setTodo({
      ...todo,
      [event.target.name]: event.target.value,
      isCompleted: false,
    });
  };

  const addTodoHandler = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (todo.title === "" || todo.title.length <= 8) {
      setError((prevState) => {
        return { ...prevState, titleError: true };
      });
    }
    if (todo.description === "" || todo.description.length <= 16) {
      setError((prevState) => {
        return { ...prevState, descError: true };
      });
    }

    if (
      todo.title === "" ||
      todo.description === "" ||
      todo.title.length < 8 ||
      todo.description.length < 16
    ) {
      return;
    }

    console.log(todo);
  };

  // fetching all the todos
  const fetchAllTodos = () => {
    axios.get("/todo/all_todos").then((res) => console.log(res));
  };

  useEffect(() => {
    fetchAllTodos();
  }, []);

  return (
    <section style={{ height: "calc(100vh - 124px)" }}>
      <Container>
        <TodoInput
          inputChangeHandler={inputChangeHandler}
          addTodoHandler={addTodoHandler}
          error={error}
        />

        <TodoHeader />

        <main>
          <Switch>
            <Route exact path="/" component={AllTodos} />
            <Route path="/completed" component={CompletedTodos} />
          </Switch>
        </main>
      </Container>
    </section>
  );
}

export default Todo;
