import axios from "axios";
import React, { useState } from "react";
import { Route, Switch } from "react-router";
import Signin from "./components/auth/Signin";
import Signup from "./components/auth/Signup";

import Layout from "./components/Layout/Layout";
import Todo from "./components/Todo/Todo";

function App() {
  const [state, setState] = useState<{
    isAuth: boolean;
    token: null | string;
    userId: null | string;
    authLoading: boolean;
    error: null | string;
  }>({
    isAuth: false,
    token: null,
    userId: null,
    authLoading: false,
    error: null,
  });

  // login handler
  const loginHandler = async (email: string, password: string) => {
    try {
      setState((prevState) => {
        return { ...prevState, authLoading: true, isAuth: true };
      });
      const { data } = await axios.post<{ token: string; userId: string }>(
        "/signin",
        { email, password }
      );

      setState({
        isAuth: true,
        token: data.token,
        userId: data.userId,
        authLoading: false,
        error: null,
      });
      console.log(data);
    } catch (error) {
      setState((prevState) => {
        return { ...prevState, isAuth: false };
      });
    } finally {
      setState((prevState) => {
        return { ...prevState, authLoading: false };
      });
    }
  };

  // signup handler
  const signupHandler = async (
    email: string,
    password: string,
    name: string
  ) => {
    try {
      setState((prevState) => {
        return { ...prevState, authLoading: true };
      });
      const { data } = await axios.post("/signup", { email, password, name });
      console.log(data);
    } catch (error) {
    } finally {
      setState((prevState) => {
        return { ...prevState, authLoading: false };
      });
    }
  };

  return (
    <div>
      <Layout>
        <Switch>
          <Route
            exact
            path="/"
            render={(props) => <Todo {...props} isAuth={state.isAuth} />}
          />
          <Route
            exact
            path="/signin"
            render={(props) => (
              <Signin
                {...props}
                loginHandler={loginHandler}
                authLoading={state.authLoading}
              />
            )}
          />
          <Route
            path="/signup"
            render={(props) => (
              <Signup
                {...props}
                signupHandler={signupHandler}
                authLoading={state.authLoading}
              />
            )}
          />
        </Switch>
      </Layout>
    </div>
  );
}

export default App;
