import React, { useEffect, useState } from "react";

import { Route, Switch } from "react-router";
import { Container } from "@material-ui/core";

import AllTodos from "./AllTodos";
import CompletedTodos from "./CompletedTodos";
import TodoHeader from "./TodoHeader";
import TodoInput from "./TodoInput";
import { AllTodoResponseType, TodoType } from "../../types/types";
import { axios } from "../../http/http";

function Todo() {
  const [todo, setTodo] = useState<{
    title: string;
    description: string;
    isCompleted: boolean;
  }>({
    title: "",
    description: "",
    isCompleted: false,
  });

  const [allTodos, setAllTodos] = useState<TodoType[]>([]);

  const [isFetchingTodos, setIsFetchingTodos] = useState(false);

  const [error, setError] = useState({
    titleError: false,
    descError: false,
  });

  const inputChangeHandler = (event: React.ChangeEvent<HTMLInputElement>) => {
    setError({ titleError: false, descError: false });
    setTodo({
      ...todo,
      [event.target.name]: event.target.value,
      isCompleted: false,
    });
  };

  const addTodoToTheDb = async () => {
    try {
      const res = await axios.post("/todo/create_todo", todo);
      console.log(res);
    } catch (err) {
      console.log(err);
    }
  };

  const addTodoHandler = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (todo.title === "" || todo.title.length <= 8) {
      setError((prevState) => {
        return { ...prevState, titleError: true };
      });
    }
    if (todo.description === "" || todo.description.length <= 16) {
      setError((prevState) => {
        return { ...prevState, descError: true };
      });
    }

    if (
      todo.title === "" ||
      todo.description === "" ||
      todo.title.length < 8 ||
      todo.description.length < 16
    ) {
      return;
    }

    addTodoToTheDb();
  };

  // fetching all the todos
  const fetchAllTodos = async () => {
    try {
      setIsFetchingTodos(true);
      const { data } = await axios.get<AllTodoResponseType>("/todo/all_todos");
      setAllTodos(data.all_todos);
    } catch (error) {
      console.log(error);
    } finally {
      setIsFetchingTodos(false);
    }
  };

  useEffect(() => {
    fetchAllTodos();
  }, []);

  return (
    <section style={{ height: "calc(100vh - 124px)" }}>
      <Container>
        <TodoInput
          inputChangeHandler={inputChangeHandler}
          addTodoHandler={addTodoHandler}
          error={error}
        />

        <TodoHeader />

        <main>
          <Switch>
            <Route
              exact
              path="/"
              render={(props) => (
                <AllTodos
                  {...props}
                  allTodos={allTodos}
                  isFetchingTodos={isFetchingTodos}
                />
              )}
            />
            <Route path="/completed" component={CompletedTodos} />
          </Switch>
        </main>
      </Container>
    </section>
  );
}

export default Todo;
