import React, { useState } from "react";

import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  CircularProgress,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import { useFormik } from "formik";

type PropsType = {
  loginHandler: (a: string, b: string) => void;
  authLoading: boolean;
};

function Signin(props: PropsType) {
  const { loginHandler, authLoading } = props;

  const [state, setState] = useState({
    email: "",
    password: "",
  });

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    onSubmit: (values) => console.log(values),
  });

  const formSubmitHandler = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (state.email === "" || state.password === "") {
      return;
    }
    loginHandler(state.email, state.password);
  };

  return (
    <Container>
      <Paper
        variant="outlined"
        style={{ padding: "1.25rem 1.875rem", marginTop: "3rem" }}
      >
        <Typography variant="h5">Signin</Typography>

        <form onSubmit={formik.handleSubmit} style={{ marginTop: "3rem" }}>
          <TextField
            color="secondary"
            fullWidth
            label="E-mail"
            style={{ marginBottom: "1.5rem" }}
            onChange={formik.handleChange}
            value={formik.values.email}
            variant="filled"
            name="email"
            required
          />
          <TextField
            color="secondary"
            fullWidth
            onChange={formik.handleChange}
            value={formik.values.password}
            label="Password"
            variant="filled"
            name="password"
            required
          />
          <Button
            style={{ marginTop: "2rem", width: "120px" }}
            variant="contained"
            color="secondary"
            disableElevation
            type="submit"
            disabled={authLoading}
          >
            {authLoading ? (
              <CircularProgress color="secondary" size={24} />
            ) : (
              "Login"
            )}
          </Button>

          <Link
            style={{ display: "block", color: "black", marginTop: "1rem" }}
            to="/signup"
          >
            Don't have an account?
          </Link>
        </form>
      </Paper>
    </Container>
  );
}

export default Signin;
