import React, { useState } from "react";

import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  CircularProgress,
} from "@material-ui/core";
import { Link } from "react-router-dom";

type PropsType = {
  signupHandler: (a: string, b: string, c: string) => void;
  authLoading: boolean;
};
function Signup(props: PropsType) {
  const { signupHandler, authLoading } = props;

  const [state, setState] = useState({
    name: "",
    email: "",
    password: "",
  });

  const formSubmitHandler = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (state.email === "" || state.password === "" || state.name === "") {
      return;
    }
    signupHandler(state.email, state.password, state.name);
  };

  return (
    <Container>
      <Paper
        variant="outlined"
        style={{ padding: "1.25rem 1.875rem", marginTop: "3rem" }}
      >
        <Typography variant="h5">Signup</Typography>

        <form onSubmit={formSubmitHandler} style={{ marginTop: "3rem" }}>
          <TextField
            color="secondary"
            fullWidth
            label="Name"
            style={{ marginBottom: "1.5rem" }}
            variant="filled"
            name="name"
            required
          />
          <TextField
            color="secondary"
            fullWidth
            label="E-mail"
            style={{ marginBottom: "1.5rem" }}
            variant="filled"
            name="email"
            required
          />
          <TextField
            color="secondary"
            fullWidth
            label="Password"
            variant="filled"
            name="password"
            required
          />
          <Button
            style={{ marginTop: "2rem", width: "120px" }}
            variant="contained"
            color="secondary"
            disableElevation
            type="submit"
            disabled={authLoading}
          >
            {authLoading ? (
              <CircularProgress color="secondary" size={24} />
            ) : (
              "Signup"
            )}
          </Button>
          <Link
            style={{ display: "block", color: "black", marginTop: "1rem" }}
            to="/signin"
          >
            Already have an account?
          </Link>
        </form>
      </Paper>
    </Container>
  );
}

export default Signup;
