import React from "react";

import { Route, Switch } from "react-router";
import { Container, Paper, TextField, makeStyles } from "@material-ui/core";

import AllTodos from "./AllTodos";
import CompletedTodos from "./CompletedTodos";
import TodoHeader from "./TodoHeader";

function Todo() {
  const classes = useStyles();
  return (
    <section style={{ height: "calc(100vh - 124px)" }}>
      <Container>
        <Paper variant="outlined" className={classes.paper}>
          <TextField label="Todo title" variant="outlined" />
          <TextField
            style={{ width: "50%", marginLeft: "1rem" }}
            label="Description"
            variant="outlined"
          />
        </Paper>

        <TodoHeader />

        <main>
          <Switch>
            <Route exact path="/" component={AllTodos} />
            <Route path="/completed" component={CompletedTodos} />
          </Switch>
        </main>
      </Container>
    </section>
  );
}

export default Todo;

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: "2rem 1rem",
    marginTop: "1rem",
  },
}));
