import React from "react";

import { Paper, TextField, makeStyles, Button, Box } from "@material-ui/core";

type PropTypes = {
  inputChangeHandler: (e: React.ChangeEvent<HTMLInputElement>) => void;
  addTodoHandler: (e: React.FormEvent<HTMLFormElement>) => void;
};

function TodoInput(props: PropTypes) {
  const classes = useStyles();

  const { inputChangeHandler, addTodoHandler } = props;

  return (
    <Paper variant="outlined" className={classes.paper}>
      <form noValidate onSubmit={addTodoHandler}>
        <Box width="100%" display="flex" alignItems="center">
          <Box width="70%">
            <TextField
              label="Todo title"
              name="title"
              variant="outlined"
              onChange={inputChangeHandler}
            />
            <TextField
              style={{ width: "70%", marginLeft: "1rem" }}
              name="description"
              label="Description"
              variant="outlined"
              onChange={inputChangeHandler}
            />
          </Box>
          <Box width="30%" display="flex" justifyContent="flex-end">
            <Button type="submit" variant="outlined" color="primary">
              Add Todo
            </Button>
          </Box>
        </Box>
      </form>
    </Paper>
  );
}

export default TodoInput;

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: "2rem 1rem",
    marginTop: "1rem",
  },
}));
