import React, { useState } from "react";

import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
} from "@material-ui/core";

type PropsType = {
  loginHandler: (a: string, b: string) => void;
  authLoading: boolean;
};

function Signin(props: PropsType) {
  const { loginHandler, authLoading } = props;

  const [state, setState] = useState({
    email: "",
    password: "",
  });

  const formSubmitHandler = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (state.email === "" || state.password === "") {
      return;
    }
    loginHandler(state.email, state.password);
  };

  return (
    <Container>
      <Paper
        variant="outlined"
        style={{ padding: "1.25rem 1.875rem", marginTop: "3rem" }}
      >
        <Typography variant="h5">Signin</Typography>

        <form onSubmit={formSubmitHandler} style={{ marginTop: "2rem" }}>
          <TextField
            color="secondary"
            fullWidth
            label="E-mail"
            style={{ marginBottom: "1.5rem" }}
            variant="filled"
            name="email"
          />
          <TextField
            color="secondary"
            fullWidth
            label="Password"
            variant="filled"
            name="password"
          />
          <Button
            style={{ marginTop: "2rem" }}
            variant="contained"
            color="secondary"
            disableElevation
            type="submit"
          >
            Disable elevation
          </Button>
        </form>
      </Paper>
    </Container>
  );
}

export default Signin;
